import React, { useState } from "react";
import "./style.css";

const ImageUploader = () => {
  const [imagePreview, setImagePreview] = useState(null); // حالة لتخزين معاينة الصورة

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader(); // كائن FileReader لقراءة الصورة

    reader.onloadend = () => {
      // تعيين عنوان URL للبيانات للصورة كمعاينة
      setImagePreview(reader.result);
    };

    if (file) {
      reader.readAsDataURL(file); // قراءة ملف الصورة كعنوان URL للبيانات
    }
  };

  return (
    <div className="image-uploader-container">
      <label htmlFor="file-upload" className="upload-button">
        تحميل الصورة
      </label>
      <input
        type="file"
        id="file-upload"
        className="file-input"
        onChange={handleImageUpload}
      />
      {/* عرض معاينة الصورة إذا كانت متوفرة */}
      {imagePreview && (
        <div className="image-preview">
          <img src={imagePreview} alt="معاينة" />
        </div>
      )}
    </div>
  );
};

export default ImageUploader;
