import React, { useState, useEffect } from "react";
import "./style.css";

const AddProduct = () => {
  const [productName, setProductName] = useState("");
  const [description, setDescription] = useState("");
  const [availableQuantity, setAvailableQuantity] = useState(1); // تعيين القيمة الابتدائية للعداد على 1
  const [productDate, setProductDate] = useState("");
  const [productImage, setProductImage] = useState("");
  const [typeOfCar, setTypeOfCar] = useState("");
  const [price, setPrice] = useState("");
  const [isSubmitClicked, setIsSubmitClicked] = useState(false);
  const [showErrorMessage, setShowErrorMessage] = useState(false);

  useEffect(() => {
    if (showErrorMessage) {
      const timer = setTimeout(() => {
        setShowErrorMessage(false);
      }, 3000); // تعيين المدة الزمنية لظهور الرسالة بشكل مؤقت (3 ثواني هنا)

      return () => clearTimeout(timer); // إلغاء تأثير التايمر عند إلغاء الكومبوننت
    }
  }, [showErrorMessage]);

  const handleSubmit = (event) => {
    event.preventDefault();
    setIsSubmitClicked(true);

    if (
      productName.trim() === "" ||
      description.trim() === "" ||
      availableQuantity === 0 || // التأكد من أن الكمية المتاحة أكبر من الصفر
      productDate.trim() === "" ||
      productImage.trim() === "" ||
      typeOfCar.trim() === "" ||
      price.trim() === ""
    ) {
      setShowErrorMessage(true); // إظهار الرسالة في حالة عدم إدخال كافة الحقول
      return;
    }

    // القيام بعملية إضافة المنتج إذا تم ملأ جميع الحقول
    console.log({
      productName,
      description,
      availableQuantity,
      productDate,
      productImage,
      typeOfCar,
      price
    });

    // إعادة تعيين قيم الحقول بعد إضافة المنتج
    setProductName("");
    setDescription("");
    setAvailableQuantity(1); // إعادة تعيين قيمة العداد إلى 1 بعد الإضافة
    setProductDate("");
    setProductImage("");
    setTypeOfCar("");
    setPrice("");
    setIsSubmitClicked(false); // إعادة تعيين قيمة الضغط على الزر إلى القيمة الافتراضية
  };

  return (
    <div className="add-product-page">
      <div className="container">
        <div className="div">
          <div className="overlap-group">
            <div className="product-info">
              <div className="product-name">Product Name:</div>
              <input
                type="text"
                className={`input-box ${isSubmitClicked && productName.trim() === "" && "red-border"}`}
                value={productName}
                onChange={(e) => setProductName(e.target.value)}
                required
              />
              <div className="description">Description:</div>
              <input
                type="text"
                className={`input-box2 ${isSubmitClicked && description.trim() === "" && "red-border"}`}
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                required
              />
              <div className="available-quantity">Available Quantity:</div>
              <div>
                <button
                  className="increment-button"
                  onClick={() => setAvailableQuantity((prev) => prev + 1)}
                >
                  +
                </button>
                <span className="quantity">{availableQuantity}</span>
                <button
                  className="decrement-button"
                  onClick={() => setAvailableQuantity((prev) => Math.max(prev - 1, 1))} // التأكد من أن الكمية لا تقل عن 1
                >
                  -
                </button>
              </div>
              <div className="product-date">Product Date:</div>
              <input
                type="date"
                className={`input-box ${isSubmitClicked && productDate.trim() === "" && "red-border"}`}
                value={productDate}
                onChange={(e) => setProductDate(e.target.value)}
                required
              />
            </div>
            <div className="product-details">
              <div className="type-of-car">Type Of Car:</div>
              <input
                type="text"
                className={`input-box4 ${isSubmitClicked && typeOfCar.trim() === "" && "red-border"}`}
                value={typeOfCar}
                onChange={(e) => setTypeOfCar(e.target.value)}
                required
              />
              <div className="price">Price:</div>
              <input
                type="number"
                className={`input-box5 ${isSubmitClicked && price.trim() === "" && "red-border"}`}
                value={price}
                onChange={(e) => setPrice(e.target.value)}
                required
              />
              <button
                type="submit"
                className="button"
                onClick={handleSubmit}
                disabled={isSubmitClicked && (
                  productName.trim() === "" ||
                  description.trim() === "" ||
                  availableQuantity === 0 ||
                  productDate.trim() === "" ||
                  productImage.trim() === "" ||
                  typeOfCar.trim() === "" ||
                  price.trim() === ""
                )}
              >
               Add Product
              </button>
              {showErrorMessage && (
                <div className="error-message">
                  All fields are required.
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddProduct;
