import React, { useState } from "react";
import "./style.css";

const AddProduct = () => {
  const [productName, setProductName] = useState("");
  const [description, setDescription] = useState("");
  const [availableQuantity, setAvailableQuantity] = useState("");
  const [productDate, setProductDate] = useState("");
  const [productImages, setProductImages] = useState([]);
  const [typeOfCar, setTypeOfCar] = useState("");
  const [price, setPrice] = useState("");
  const [isSubmitClicked, setIsSubmitClicked] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();
    setIsSubmitClicked(true);

    if (
      productName.trim() === "" ||
      description.trim() === "" ||
      availableQuantity.trim() === "" ||
      productDate.trim() === "" ||
      productImages.length === 0 || // تحقق من وجود صور محددة
      typeOfCar.trim() === "" ||
      price.trim() === ""
    ) {
      return; // لا تقم بمتابعة العملية إذا كان أي من الحقول فارغة
    }

    console.log({
      productName,
      description,
      availableQuantity,
      productDate,
      productImages,
      typeOfCar,
      price
    });

    setProductName("");
    setDescription("");
    setAvailableQuantity("");
    setProductDate("");
    setProductImages([]);
    setTypeOfCar("");
    setPrice("");
    setIsSubmitClicked(false);
  };

  // تابع لمعالجة تحميل الصور
  const handleImageUpload = (event) => {
    const selectedImages = Array.from(event.target.files);
    setProductImages(selectedImages);
  };

  return (
    <div className="add-product-page">
      <div className="container">
        <div className="div">
          <div className="overlap-group">
            <div className="product-info">
              {/* مربعات إدخال المعلومات */}
            </div>
            <div className="product-details">
              {/* مربعات إدخال المعلومات */}
            </div>
            {/* خانة اختيار الصور */}
            <div className="image-upload">
              <input
                type="file"
                accept="image/*"
                multiple
                onChange={handleImageUpload}
              />
              <label htmlFor="image-upload">Choose Images</label>
            </div>
            {/* مكان عرض الصور المحددة */}
            <div className="selected-images">
              {productImages.map((image, index) => (
                <img
                  key={index}
                  src={URL.createObjectURL(image)}
                  alt={`Product ${index + 1}`}
                  className="selected-image"
                />
              ))}
            </div>
          </div>
          <button
            type="submit"
            className="button"
            onClick={handleSubmit}
            disabled={isSubmitClicked && (
              productName.trim() === "" ||
              description.trim() === "" ||
              availableQuantity.trim() === "" ||
              productDate.trim() === "" ||
              productImages.length === 0 ||
              typeOfCar.trim() === "" ||
              price.trim() === ""
            )}
          >
            Add Product
          </button>
          {isSubmitClicked && (
            <div className="error-message">
              All fields are required.
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AddProduct;
