import React, { useState } from "react";
import "./style.css";

const AddProduct = () => {
  const [productName, setProductName] = useState("");
  const [description, setDescription] = useState("");
  const [availableQuantity, setAvailableQuantity] = useState(1);
  const [productDate, setProductDate] = useState("");
  const [productImages, setProductImages] = useState([]); // استخدام قائمة لتخزين الصور
  const [typeOfCar, setTypeOfCar] = useState("");
  const [price, setPrice] = useState("");
  const [isSubmitClicked, setIsSubmitClicked] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();
    setIsSubmitClicked(true);

    if (
      productName.trim() === "" ||
      description.trim() === "" ||
      availableQuantity <= 0 || // التأكد من أن الكمية أكبر من صفر
      productDate.trim() === "" ||
      productImages.length === 0 || // التأكد من أنه تم اختيار صور
      typeOfCar.trim() === "" ||
      price.trim() === ""
    ) {
      return;
    }

    console.log({
      productName,
      description,
      availableQuantity,
      productDate,
      productImages,
      typeOfCar,
      price
    });

    // إعادة تعيين الحالة بعد الإرسال
    setProductName("");
    setDescription("");
    setAvailableQuantity(1);
    setProductDate("");
    setProductImages([]);
    setTypeOfCar("");
    setPrice("");
    setIsSubmitClicked(false);
  };

  const handleImageChange = (event) => {
    const selectedImages = Array.from(event.target.files); // قائمة الصور المختارة
    setProductImages(selectedImages);
  };

  return (
    <div className="add-product-page">
      <div className="container">
        <div className="div">
          <div className="overlap-group">
            <div className="product-info">
              {/* باقي الحقول */}
            </div>
            <div className="product-details">
              <div className="type-of-car">Type Of Car:</div>
              <input
                type="text"
                className={`input-box4 ${
                  isSubmitClicked && typeOfCar.trim() === "" && "red-border"
                }`}
                value={typeOfCar}
                onChange={(e) => setTypeOfCar(e.target.value)}
                required
              />
              <div className="price">Price:</div>
              <input
                type="number"
                className={`input-box5 ${
                  isSubmitClicked && price.trim() === "" && "red-border"
                }`}
                value={price}
                onChange={(e) => setPrice(e.target.value)}
                required
              />
              <div className="image-upload-container">
                <input
                  type="file"
                  multiple // يسمح باختيار أكثر من صورة واحدة
                  onChange={handleImageChange}
                />
                <button
                  className="button"
                  onClick={handleSubmit}
                  disabled={isSubmitClicked}
                >
                  Add Product
                </button>
              </div>
              {isSubmitClicked && (
                <div className="error-message">
                  All fields are required.
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddProduct;
