import React, { useState } from "react";
import "./style.css";

const AddProduct = () => {
  const [productName, setProductName] = useState("");
  const [description, setDescription] = useState("");
  const [availableQuantity, setAvailableQuantity] = useState("");
  const [productDate, setProductDate] = useState("");
  const [productImage, setProductImage] = useState("");
  const [typeOfCar, setTypeOfCar] = useState("");
  const [price, setPrice] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();

    console.log({
      productName,
      description,
      availableQuantity,
      productDate,
      productImage,
      typeOfCar,
      price
    });

    setProductName("");
    setDescription("");
    setAvailableQuantity("");
    setProductDate("");
    setProductImage("");
    setTypeOfCar("");
    setPrice("");
  };

  const isFormValid = () => {
    return (
      productName.trim() !== "" &&
      description.trim() !== "" &&
      availableQuantity.trim() !== "" &&
      productDate.trim() !== "" &&
      productImage.trim() !== "" &&
      typeOfCar.trim() !== "" &&
      price.trim() !== ""
    );
  };

  return (
    <div className="add-product-page">
      <div className="container">
        <div className="div">
          <div className="overlap-group">
            <div className="product-info">
              <div className="product-name">Product Name:</div>
              <input
                type="text"
                className="input-box"
                value={productName}
                onChange={(e) => setProductName(e.target.value)}
                required
              />
              <div className="description">Description:</div>
              <input
                type="text"
                className="input-box2"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                required
              />
              <div className="available-quantity">Available Quantity:</div>
              <input
                type="text"
                className="input-box3"
                value={availableQuantity}
                onChange={(e) => setAvailableQuantity(e.target.value)}
                required
              />
              <div className="product-date">Product Date:</div>
              <input
                type="date"
                className="input-box"
                value={productDate}
                onChange={(e) => setProductDate(e.target.value)}
                required
              />
            </div>
            <div className="product-details">
              <div className="type-of-car">Type Of Car:</div>
              <input
                type="text"
                className="input-box4"
                value={typeOfCar}
                onChange={(e) => setTypeOfCar(e.target.value)}
                required
              />
              <div className="price">Price:</div>
              <input
                type="number"
                className="input-box5"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
                required
              />
              <button
                type="submit"
                className="button"
                onClick={handleSubmit}
                disabled={!isFormValid()} // تعطيل الزر إذا كانت أي من الحقول فارغة
              >
               Add Product
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddProduct;
